namespace Swag\PayPal\Storefront\Controller;

use OpenApi\Attributes as OA;
use Shopware\Core\Framework\Log\Package;
use Shopware\Core\System\SalesChannel\SalesChannelContext;
use Swag\PayPal\Setting\Service\CredentialsUtilInterface;
use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\Routing\Annotation\Route;

#[Package('checkout')]
#[Route(defaults: ['_routeScope' => ['storefront']])]
class ApplePayController extends AbstractController
{
    public const SANDBOX_TOKEN = '';

    public const LIVE_TOKEN = '';

    /**
     * @internal
     */
    public function __construct(
        private readonly CredentialsUtilInterface $credentialsUtil,
    ) {
    }

    #[OA\Get(
        path: '/.well-known/apple-developer-merchantid-domain-association',
        operationId: 'applePayDomainAssociation',
        description: 'Return the Apple Pay developer association',
        tags: ['PayPal', 'Apple Pay'],
        responses: [new OA\Response(
            response: Response::HTTP_OK,
            description: 'Apple developer token',
        )]
    )]
    #[Route(path: '/.well-known/apple-developer-merchantid-domain-association', name: 'paypal.apple_pay.domain_association', methods: ['GET'])]
    public function getApplePayDomainAssociation(SalesChannelContext $context): Response
    {
        if ($this->credentialsUtil->isSandbox($context->getSalesChannelId())) {
            return new Response(self::SANDBOX_TOKEN);
        }

        return new Response(self::LIVE_TOKEN);
    }
}
