namespace Swag\PayPal\Test\Pos\Api;

use PHPUnit\Framework\Attributes\DataProvider;
use PHPUnit\Framework\TestCase;
use Shopware\Core\Framework\Log\Package;
use Swag\PayPal\Pos\Api\Exception\InvalidApiKeyException;
use Swag\PayPal\Pos\Api\Service\ApiKeyDecoder;

/**
 * @internal
 */
#[Package('checkout')]
class ApiKeyDecoderTest extends TestCase
{
    private const EXAMPLE_API_KEY = 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IjAifQ..VkXyBzrEOeUM1K9w5NhYqumcfShm738LMJG3JMW3FENrM90eGMZxfYaoY3jFYws2MkjktGShsf_8LQ4ZqDzeetREWQ8A0DPN2_0GXqbf-jZVGFwCR_Oxy2FBrBcIdjmeQMq_cX4siFd0aAxAcraA5IJIng81Jx1SEu4aA72apGylqW1l3oZ1YUXNgUd9zOj5OKPK_uhxMSLyJ8MD_fyXQH8BDUxJ8Y4dByJYDkXOzHz1C-uWEVrhIJ0OGVmEnh1Cxq2gtKyjQcz3rMZg2VN52GY_Yx2AcWlnjiwxf0nlMVSHegKyGfnVoyXIw-H4T2mA_R0NmixxT7teJ8NsPTd9NQ';
    private const EXAMPLE_CLIENT_ID = '59164ce0-b166-11ea-8091-5e66ce58b3fa';

    public function testClientId(): void
    {
        $decoder = new ApiKeyDecoder();

        $decoded = $decoder->decode(self::EXAMPLE_API_KEY);

        static::assertSame(self::EXAMPLE_CLIENT_ID, $decoded->getPayload()->getClientId());
    }

    public static function dataProviderMalformedSegments(): array
    {
        return [
            [0, 'header'],
            [1, 'payload'],
            [2, 'signature'],
            [3, 'number of segments'],
        ];
    }

    #[DataProvider('dataProviderMalformedSegments')]
    public function testMalformed(int $segmentOrder, string $segmentName): void
    {
        $decoder = new ApiKeyDecoder();

        $parts = \explode('.', self::EXAMPLE_API_KEY);
        $parts[$segmentOrder] = 'MI$$ING';

        $this->expectException(InvalidApiKeyException::class);
        $this->expectExceptionMessage("The given API key is invalid. The {$segmentName} is incorrect.");

        $decoder->decode(\implode('.', $parts));
    }
}
